# get the most repeated K-mers (for each length ranging from 1 to 10) and their occurences,

genome_vibrio_cholerae = ""

most_frequent_patterns = {}

for i in range(10):
    most_frequent_patterns[i] = MostFrequentPatterns(genome_vibrio_cholerae, i)

for i in most_frequent_patterns.keys():
    for pattern in most_frequent_patterns[i]:
        print(str(pattern) + " : " + str(PatternCount(genome_vibrio_cholerae, pattern)))
